--
-- TOC entry 333 (class 1255 OID 17041)
-- Name: tr_view_tr_check(); Type: FUNCTION; Schema: framework; Owner: postgres
--

CREATE FUNCTION tr_view_tr_check() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
BEGIN
	/*CONFIG CHECKER*/
        /*check title uniq*/
        IF
          (SELECT
              count(*)
           FROM (
            SELECT
              count(*) as k,
              p.title
            FROM
              (SELECT
                  value->>'title' as title
              FROM json_array_elements(NEW.config)) as p
              GROUP BY title
              HAVING count(*) > 1) as pz) > 0 
        THEN
            PERFORM raiserror('"title" in config must be uniq');
        END IF;
        
        
        /*check t in config - allias must be if not function*/
        IF 
            (SELECT
                  count(*)
             FROM json_array_elements(NEW.config)
             WHERE (value->>'fn') is null and
                   (value->>'t') is null 
             ) > 0 
        THEN
            PERFORM raiserror('Can''t find "t" for some columns in config');
        END IF;    

    	
        /*check not null params*/
        IF 
          (SELECT
              count(*)
          FROM json_array_elements(NEW.config)
          WHERE
              coalesce((value->>'col'),'') = '' or
              coalesce((value->>'title'),'') = '' or
              (value->>'visible') is null or 
              (value->>'type') is null
          ) > 0
        THEN
            PERFORM raiserror('"col", "title", "type", or "visible" can''t be empty');	
        END IF;
        
        /*check title length*/
        IF 
          (SELECT
              count(*)
          FROM json_array_elements(NEW.config)
          WHERE
              length(coalesce((value->>'title'),'')) > 33
          ) > 0
        THEN
            PERFORM raiserror('"title" length must be <= 33 char');	
        END IF;
        
    /*CONFIG CHECKER*/
    
    
    /*ACTS CHECKER*/
        /*check not null values*/
        IF
         (SELECT
          	count(*)
          FROM json_array_elements(NEW.acts)
          WHERE 
            COALESCE((value->>'title'),'') = '' or
            COALESCE((value->>'type'),'') = '' or
            (value->>'isforevery') is null 
          ) > 0
        THEN
        	PERFORM raiserror('In acts "title", "type", "isforevery" can''t be empty');
    	END IF;
    
    	/*check act (url) for link, api*/
        IF
         (SELECT
          	count(*)
          FROM json_array_elements(NEW.acts)
          WHERE upper((value->>'type')) in ('LINK', 'API')
          		and 
            coalesce((value->>'act'),'') = '' 
          ) > 0
        THEN
        	PERFORM raiserror('In acts types (link, api) "act" can''t be empty!');
    	END IF;
        
        /*check act title*/
        IF
          (SELECT
              count(*)
           FROM (
            SELECT
              count(*) as k,
              p.title
            FROM
              (SELECT
                  value->>'title' as title
              FROM json_array_elements(NEW.acts)) as p
              GROUP BY title
              HAVING count(*) > 1) as pz) > 0 
        THEN
        	PERFORM raiserror('In acts "title" must be uniq!');
    	END IF;
        
    /*ACTS CHECKER*/
    
    RETURN NEW;

END;
$$;


