    LANGUAGE plpgsql
    AS $$
DECLARE
  _config json;
  _title varchar(350);
  _path varchar(350);
  _userid int;
  _newdata json;
BEGIN
  -- INSERT/UPDATE COMPOSITION

  _config = injson->>'config';
  _title = injson->>'title';
  _path = injson->>'path';
  _id = injson->>'id';
  _userid = injson->>'userid';
  
  IF _id is null THEN
  	IF _config is null THEN
    	perform raiserror('config is null');
    END IF;

  	IF _title is null THEN
    	perform raiserror('title is null'); 
    END IF;

  	IF _path is null THEN
        perform raiserror('path is null');
    END IF;
    
    _id = nextval('framework.compos_id_seq'::regclass);
    
    INSERT INTO framework.compos (
      id, title,
      path, config
    )
    VALUES (
      _id, _title,
      _path, _config
    );
    
    
    SELECT row_to_json(d)
    FROM (
	  SELECT
		  *
	  FROM framework.compos
	) as d
    WHERE id = _id
    INTO _newdata;
    
  	INSERT INTO framework.logtable (
      tablename, tableid,
      opertype, userid,
      newdata
    ) VALUES (
      'framework.compos', _id::varchar(150),
      '1', _userid ,
     _newdata   
    );    
  ELSE
    SELECT row_to_json(d)
    FROM ( 
	  SELECT
		  *
	  FROM framework.compos
	) as d
    WHERE id = _id
    INTO _newdata;
    
  	UPDATE framework.compos
    SET
      title = coalesce(_title,title),
      path = coalesce(_path,path),
      config = coalesce(_config,config )  	
    WHERE id = _id;
    
  	INSERT INTO framework.logtable (
      tablename, tableid,
      opertype, userid,
      newdata
    ) VALUES (
      'framework.compos', _id::varchar(150),
      '2', _userid,
     _newdata   
    ); 
  END IF;    
END;
$$;


