CREATE FUNCTION fn_config_relationcolumns(_id integer, OUT relation_columns character varying) RETURNS character varying
    LANGUAGE plpgsql
    AS $$
DECLARE 
	_relationcolums JSON;
    _multicolums JSON;
    
