  FROM (
  	SELECT
  		*
 	FROM framework.dialog_messages 
 	WHERE id = _id
  ) as d
  INTO _od;
  
  DELETE FROM framework.dialog_notifications 
  WHERE message_id = _id;
  
  DELETE FROM framework.dialog_messages 
  WHERE id = _id;
  
  -- LOG
  INSERT INTO framework.logtable (
    tablename, tableid, opertype,
