  	SELECT
 	 array_to_json(
       ARRAY(
        SELECT
            *
        FROM json_array_elements(_files)
        WHERE lower(value->>'content_type') like 'image%'
       )
     )
  );
  
  _files = (
  	SELECT
 	 array_to_json(
       ARRAY(
        SELECT
            *
        FROM json_array_elements(_files)
        WHERE lower(value->>'content_type') not like 'image%'
       )
     )
  );
  
  -- CHECKS
  IF _userid is NULL 
