    WHERE q.value = '1'
    INTO _issysadmin;

	--     
	SELECT
    	array_to_json(array_agg(row_to_json(pz)))
        FROM (
			SELECT 
            	ro.id AS "value",
  				ro.rolename AS "label"
			FROM framework.roles AS ro
            WHERE ro.id <> 0 			-- 0  ID  
            ) AS pz
	INTO outjson;
    
    
    --         
	IF _issysadmin IS NULL THEN
	--         
        SELECT
