      SELECT 
          COUNT(p.oid)::varchar
      FROM pg_proc as p
          JOIN pg_namespace ns ON (p.pronamespace = ns.oid)
      WHERE ns.nspname = fn_title
    ), '"');
    
    squery = CONCAT('
      CREATE FUNCTION ',fn_title,' ()
      RETURNS void AS
      $body$
      DECLARE BEGIN END;
      $body$
      LANGUAGE ''plpgsql'' VOLATILE CALLED ON NULL INPUT SECURITY INVOKER;
    ');
    
    
    EXECUTE squery;
    SELECT 
        p.oid
    FROM pg_proc as p
        JOIN pg_namespace ns ON (p.pronamespace = ns.oid)
    WHERE concat('"',ns.nspname,'"."',p.proname,'"') = fn_title
    	and p.oid not in (
        	SELECT id FROM sqlmanager.functionslist 
        )
    INTO _id;
    
    INSERT INTO sqlmanager.functionslist(
      id, functionschema,
      functiontitle,
      fullname, descr,
      userid, functionfulltitle, 
      dll
    )
    SELECT 
        p.oid, ns.nspname, p.proname, 
        concat(ns.nspname,'.', p.proname,'(', (
          	SELECT 
            	string_agg(
                	CONCAT(
                    	an.arg,' ', (
                        	CASE WHEN 
                            am.mode = 'o'
                            THEN 'out'
                            ELSE ''
                            END
                        ),' ',p.typname
                    ),','
                ) 
           	FROM (
              SELECT 
                  row_number() over (order by 0) as rownum, c.typname
              FROM json_array_elements_text(
