CREATE FUNCTION fn_autocomplete(injson json, OUT outjson json) RETURNS json
    LANGUAGE plpgsql
    AS $_$
DECLARE
  col varchar;
 -- val varchar;
  _table varchar;
  squery varchar;
  _val varchar;
  _viewid int;
BEGIN
	-- FORM AUTOCOMPLETE METHOD
	col = injson->>'col';
    _val = injson->>'val';
    _viewid = injson->>'viewid';
    _table = injson->>'table';
    
    IF COALESCE(_table,'') = ''
    THEN
    	SELECT 
        	tablename
        FROM framework.views
        WHERE id = _viewid
        INTO _table;
    END IF;
    --perform raiserror(val);
    IF _val is not null and length(_val) > 0 
    THEN
    	_val = concat('%',upper(_val),'%');
    	squery = concat(squery, '
        	SELECT array_to_json(array_agg(row_to_json(d))) 
            FROM (
            	SELECT distinct ' , 
            		col , ' as value, ' , col , '  as label 
                FROM ' , _table , ' 
                WHERE upper(', col, ')::varchar like $1::varchar 
                LIMIT 500
            ) as d'
        );
            	
       EXECUTE format(squery) USING _val INTO outjson;
       
       outjson = COALESCE(
           outjson, (
               SELECT array_to_json(array_agg(row_to_json(d))) 
               FROM (    
               		SELECT 
                    	injson ->> 'val' as value, 
               			injson ->> 'val' as label          
              ) as d
           )
       );
    END IF;        

	outjson = COALESCE(outjson, '[]');
    outjson = '{"label":"","value":null}'::jsonb||outjson::jsonb; 

END;
$_$;


