CREATE FUNCTION fn_branchestree_recurs(_parentid integer, _treesid integer, OUT outjson json) RETURNS json
    LANGUAGE plpgsql
    AS $$
BEGIN
	-- TREES BRANCHES RECURS FUNCTION
	SELECT
    	array_to_json(array_agg(row_to_json(d))) 
    FROM (
    	SELECT
          tb.id as key, tb.icon, tb.parentid,
          tb.treesid, tb.title as label, tb.ismain,
          COALESCE(v.title, c.title) as component_title,
          COALESCE(v.viewtype, 'composition') as component_type,
