          	SELECT
            	row_to_json(zd)
            FROM (
            	SELECT
                	u.id,
                    u.login,
                    u.photo
                FROM framework.users as u
                WHERE u.id = d.creator
            ) as zd
          ) as creator,
          d.dialog_admins, (
            CASE WHEN (
              SELECT
                  count(*)
              FROM json_array_elements_text(d.dialog_admins)
