CREATE FUNCTION fn_htmldatatype(sqldatatype character varying, OUT htmltype character varying) RETURNS character varying
    LANGUAGE plpgsql
    AS $$
BEGIN
	
    htmltype = 'text';
	IF sqldatatype in (
    	'int', 'integer', 'smallint', 'real',  'money', 'float', 'decimal', 
        'numeric', 'smallmoney', 'bigint', 'double precision'
    ) 
    THEN
    	htmltype = 'number';
    END IF;     
	
    IF sqldatatype in (
