CREATE FUNCTION fn_paramtypes(injson json, OUT outjson json) RETURNS json
    LANGUAGE plpgsql
    AS $$
BEGIN

	SELECT 
    	array_to_json(array_agg(row_to_json(d)))
    FROM (
	  SELECT
		  a.val as label,
		  a.val as value
	  FROM framework.paramtypes as a
	) as d
    INTO outjson;
    
    outjson = coalesce(outjson,'[]');
    

END;
$$;


