CREATE FUNCTION fn_roles_fetch(injson json, OUT outjson json) RETURNS json
    LANGUAGE plpgsql
    AS $$
DECLARE
	_userid int;
    _roles JSON;		
    _isdev int;			
    _issysadmin int;	
BEGIN

	_isdev = 0;
	_userid = injson->>'userid';
    

	SELECT    	
    	u.roles 
	FROM
    	framework.users AS u
	WHERE u.id = _userid
	INTO _roles;

    SELECT
    	1
    FROM
    	json_array_elements_text(_roles) as q
    WHERE q.value = '0'
    INTO _isdev;
    
    SELECT
    	1
    FROM
    	json_array_elements_text(_roles) as q
    WHERE q.value = '1'
    INTO _issysadmin;

	SELECT
    	array_to_json(array_agg(row_to_json(pz)))
        FROM (
			SELECT 
            	ro.id AS "value",
  				ro.rolename AS "label"
			FROM framework.roles AS ro
            WHERE ro.id <> 0 			
            ) AS pz
	INTO outjson;
    
    
	IF _issysadmin IS NULL 
    THEN
	
        SELECT
            array_to_json(array_agg(row_to_json(pz)))
        FROM (
        	SELECT
            	(o->>'value')::int AS "value",
                 o->>'label' AS "label"
            FROM json_array_elements(outjson) AS o
            WHERE o->>'value' <> '1'		
        ) AS pz
        INTO outjson;
    END IF;

    outjson = COALESCE(outjson, '[]');
END;
$$;


