CREATE FUNCTION fn_trees_bypath(injson json, OUT outjson json) RETURNS json
    LANGUAGE plpgsql
    AS $$
DECLARE
  _userid int;
  _path varchar(350);
  _roles json;
BEGIN
  _userid = injson->>'userid';
  _path = injson->>'path';
  SELECT
  	u.roles
  FROM framework.users as u
  WHERE u.id = _userid
  INTO _roles;
  
  
  SELECT 
  	row_to_json(d)
  FROM (
    SELECT 
      t.id, t.title, t.descr, t.url as "path", (
        SELECT 
          array_to_json(array_agg(row_to_json(t))) 
        FROM (
             SELECT 
                ta.*
             FROM framework.treesacts as ta
             WHERE coalesce(ta.act, '') <> '' and 	   		 
                   coalesce(ta.title, '') <> '' and 
                   ta.treesid = t.id and ((
                    	SELECT count(value) 
                        FROM json_array_elements(ta.roles)
                    ) = 0 OR (
                    	SELECT count(r1.value) 
                        FROM json_array_elements_text(ta.roles) as r1
                        	JOIN json_array_elements_text(_roles) as r2 ON r1.value::varchar = r2.value::varchar
                    ) > 0 OR (
                    	SELECT count(r1.value) 
                        FROM json_array_elements_text(_roles) as r1
                        WHERE r1.value::varchar = '1'
                    ) > 0
                   )
        ) as t
      ) as acts,   
      framework.fn_branchestree_recurs(0, t.id) as branches, (
          SELECT
            array_to_json(array_agg(row_to_json(d))) 
          FROM (
            SELECT
                tb.id as key, tb.icon, tb.parentid, tb.treesid,
                tb.title as label, tb.treeviewtype, 
                coalesce(v.path,c.path) as path, v.viewtype,
                COALESCE(v.viewtype, 'composition') as component_type,  tb.ismain,  
                COALESCE(v.title, c.title) as component_title, tb.visible
            FROM framework.treesbranches as tb
              LEFT JOIN framework.views as v on v.id = tb.viewid
              LEFT JOIN framework.compos as c on c.id = tb.compoid
            WHERE tb.treesid = t.id and tb.title is not null AND (
                    tb.roles is NULL OR (
                        SELECT count(t1.value)
                        FROM json_array_elements_text(tb.roles) as t1
                        JOIN json_array_elements_text(_roles) as t2 on 
                                 t1.value::varchar::int = t2.value::varchar::int or 
                                 t2.value::varchar::int = '0'
                    ) > 0
                  )
            ORDER BY tb.orderby
         ) as d
      ) as  items  
    FROM framework.trees as t
    WHERE t.url = _path and (t.roles is null or (
                SELECT count(*)
                FROM json_array_elements_text(t.roles) as t1
              ) = 0 or (
                SELECT count(t1.value)
                FROM json_array_elements_text(t.roles) as t1
                JOIN json_array_elements_text(_roles) as t2 on 
                         t1.value::varchar::int = t2.value::varchar::int or 
                         t2.value::varchar::int = '0'
              ) > 0
           )
  ) as d
  INTO outjson;
  
  outjson = coalesce(outjson,'{}');
  
END;
$$;


