    ALTER TABLE framework.select_condition
      ENABLE TRIGGER ALL;
      
    ALTER TABLE framework.visible_condition
      ENABLE TRIGGER ALL;    
      
    ALTER TABLE framework.act_parametrs
      ENABLE TRIGGER ALL; 
         
    ALTER TABLE framework.act_visible_condions
