CREATE FUNCTION "fn_view_getByPath"(_path character varying, _viewtype character varying, OUT outjson json) RETURNS json
    LANGUAGE plpgsql
    AS $$
--DECLARE
  --_calendar_actions_cols varchar(500); -- FOR ERROR
 -- _relation varchar(250);
 -- _relation_columns json;
BEGIN
  IF (
      SELECT count(v.id)  
      FROM framework.views as v
      WHERE v."path" = _path 
  ) = 0
  THEN
      PERFORM raiserror('view is not found');
  END IF;
 
 IF _viewtype = 'list' and (
 		SELECT count(v.id)  
    	FROM framework.views as v
      	WHERE v."path" = _path and v.viewtype in ('table','tiles','api_table','calendar')
 ) = 0
 THEN
     PERFORM raiserror('view with type list is not found');
 END IF;
 
  IF _viewtype = 'getone' and (
      SELECT count(v.id)  
      FROM framework.views as v
      WHERE v."path" = _path and v.viewtype like '%form%'
  ) = 0
  THEN
      PERFORM raiserror('view with type getone is not found');
  END IF; 
 
  IF (
      SELECT v.viewtype 
      FROM framework.views as v
      WHERE v."path" = _path 
  ) = 'calendar' 
  THEN
       -- Calendar checks
      
      IF (
          SELECT count(c.id) 
          FROM framework.views as v
              JOIN framework.config as c on c.viewid = v.id and c.visible and c.type = 'calendarStartDate'
          WHERE v."path" = _path 
      ) = 0
      THEN
          PERFORM raiserror('view with type "calendar" must have one column typeof "calendarStartDate"');
      END IF;
      
      
      IF (
          SELECT count(c.id) 
          FROM framework.views as v
              JOIN framework.config as c on c.viewid = v.id and c.visible and c.type = 'calendarEndDate'
          WHERE v."path" = _path 
      ) = 0
      THEN
           PERFORM raiserror('view with type "calendar" must have one column typeof "calendarEndDate"');
      END IF;
      
      IF (
          SELECT count(c.id) 
          FROM framework.views as v
              JOIN framework.config as c on c.viewid = v.id and c.visible and c.type = 'calendarTitle'
          WHERE v."path" = _path 
      ) = 0
      THEN
           PERFORM raiserror('view with type "calendar" must have one column typeof "calendarTitle"');
      END IF;
  END IF;
 
  SELECT
     row_to_json(d)
  FROM (
     SELECT
         v.id, v.title,
         v.pagecount, v.pagination,
         v.checker, v.classname,
         v.orderby, v.ispagesize, v.rmnu,
         v.subscrible, COALESCE((
             SELECT
                array_to_json(array_agg(row_to_json(d)))
             FROM (
                SELECT
                  value as value,
                  value as label
                FROM json_array_elements(v.roles) as rl) as d
         ),'[]') as roles, v.viewtype,
         v.tablename, COALESCE((
            SELECT
                array_to_json(array_agg(row_to_json(d)))
            FROM (
                SELECT
                    f.title, f.id, f.type, f.t, f.classname,
                    c.col as column,
                    CASE WHEN f.columns is not null
                    THEN
                    COALESCE((
                      SELECT
                        array_to_json(array_agg(row_to_json(d)))
                      FROM (
                          SELECT
                              cc.col as label,
                              cc.title as value, (
                                 CASE WHEN v.viewtype not like '%api_%'  
                                 THEN concat(cc.col,'_',cc.id::varchar)
                                 ELSE cc.col
                                 END
							  ) as key,
                              CASE WHEN cc.related THEN cc.t
                              ELSE '1'
                              END as t
                          FROM json_array_elements_text(f.columns) as ff
                              JOIN framework.config as cc on cc.viewid = v.id and (
                                   cc.title = ff.value::varchar OR cc.id::varchar = ff.value::varchar
                              )
                      ) as d),'[]'
                    )
                    ELSE
                      null
                    END as columns,           
                    COALESCE((
                      SELECT
                           array_to_json(array_agg(row_to_json(d)))
                      FROM (
                          SELECT
                              value as value,
                              value as label
                          FROM json_array_elements(f.roles) as rl) as d),'[]'
                    ) as roles, f."table", f."position"
                FROM framework.filters as f
                    LEFT JOIN framework.config as c on c.id = f.val_desc
                WHERE f.viewid = v.id
                ORDER BY f.column_order
            ) as d), '[]'
         ) as filters,
         COALESCE((
             SELECT
                 array_to_json(array_agg(row_to_json(d)))
             FROM (
                   SELECT
                       a.act_url as act, a.title, a.icon, a.classname,
                       a.act_type as "type", a.main_action as ismain,
                       COALESCE((
                           SELECT
                               array_to_json(array_agg(row_to_json(d)))
                           FROM (
                               SELECT
                                   value as value, value as label
                               FROM json_array_elements(a.roles) as rl
                           ) as d
                       ),'[]') as roles,
                       a.forevery as isforevery,
                       a.ask_confirm as actapiconfirm,
                       a.refresh_data as actapirefresh,
                       upper(a.api_type) as actapitype,
                       a.api_method as actapimethod, (
                           SELECT
                               array_to_json(array_agg(row_to_json(d)))
                           FROM (
                                SELECT
                                    CASE WHEN ap.val_desc is not null
                                    THEN
                                    json_build_object(
                                        'value',cc.title,
                                        'label',cc.title,
                                        't', cc.t,
                                        'key', (
                                          CASE WHEN v.viewtype not like '%api_%'  
                                          THEN concat(cc.col,'_',cc.id::varchar)
                                          ELSE cc.col
                                          END
							  		  )
                                    )
                                    ELSE
                                        null
                                    END as paramcolumn,
                                    ap.paramconst, ap.paraminput, ap.paramt,
                                    ap.paramtitle, ap.query_type
                                 FROM framework.act_parametrs as ap
                                      LEFT JOIN framework.config as cc on cc.id = ap.val_desc
                                 WHERE ap.actionid = a.id
                                 ORDER BY ap.orderby
                           ) as d
                       ) as parametrs, (
                           SELECT
                               array_to_json(array_agg(row_to_json(d)))
                           FROM (
                              SELECT
                                  av.value,
                                  json_build_object(
                                      'label',cc.col,
                                      't',cc.t,
                                      'value', cc.title,
                                      'key',  (
                                          CASE WHEN v.viewtype not like '%api_%'  
                                          THEN concat(cc.col,'_',cc.id::varchar)
                                          ELSE cc.col
                                          END
							  		  )
                                  ) as col,
                                  json_build_object('value',op.value,'js',op.js) as operation
                              FROM framework.act_visible_condions as av
                                  LEFT JOIN framework.operations as op on op.value = av.operation
                                  LEFT JOIN framework.config as cc on cc.viewid = v.id and cc.id = av.val_desc
                              WHERE av.actionid = a.id
                           ) as d
                       ) as act_visible_condition ,
                       a.sps, a.parent_id, a."position", (
                         CASE WHEN a."position" = '2' AND (
                           SELECT count(pa.id) 
                           FROM framework.actions as pa
                           WHERE pa.parent_id = a.id
                         ) > 0
                         THEN framework.fn_actions_recurs(a.id, v.id, v.viewtype)
                         ELSE '[]'
                         END                           
                       ) as childs
                 FROM framework.actions as a
                 WHERE a.viewid = v.id AND a.parent_id is null
                 ORDER BY a.column_order
              ) as d ),'[]'
          ) as acts,
          framework.fn_config_to_json(v.id) as config
    FROM framework.views as v
    WHERE v."path" = _path
 ) as d
 INTO outjson;

END;
$$;


