CREATE FUNCTION tr_actions_tr() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
BEGIN
	IF NEW.act_type in ('API', 'onLoad') and 
       NEW.api_type is NULL 
    THEN
    	NEW.api_type = 'get';	
        NEW.classname = coalesce(NEW.classname,'success');
    END IF;
    
    IF NEW.act_type = 'Save' 
    THEN
    	NEW.forevery = false;
        NEW.classname = coalesce(NEW.classname,'success');
    END IF; 
	
    IF NEW.act_type in ('Delete', 'Expand') 
    THEN
    	NEW.forevery = true;
        NEW.classname = coalesce(NEW.classname,'danger');
    END IF; 

    
    IF NEW.act_type = 'Expand' and not (
    	NEW.act_url like '/list/%' OR 
		NEW.act_url like '/getone/%'
      )    
    THEN
    	PERFORM raiserror('Expand act must have list or getone url');
    END IF;
    
    IF NEW.position NOT IN (2) AND NEW.parent_id is NOT NULL
    THEN
    	PERFORM raiserror('This position can not have parent item');
    END IF;
    
    IF NEW.parent_id IS NOT NULL
    THEN
    	NEW.position = '2';
    END IF;
    
	RETURN NEW;
END;
$$;


