  NEW.dll = CONCAT(
  	'/*
    //PLEASE DROP FUNCTION IF YOU CHANGE ARGUMENTS
    DROP FUNCTION 
', NEW.fullname ,' 
*/
', 
NEW.dll
  );
  
  NEW.definition = SUBSTRING(NEW.dll,
	POSITION('$function$' IN NEW.dll) + 10,
    length(NEW.dll)
  );
  
  NEW.definition =  SUBSTRING(NEW.definition,
