--

CREATE FUNCTION tr_queries_tr_ins() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
    _starttime time;
	_endtime time;
	res json;
	query text;
	tr json;
BEGIN
    NEW.querytext = COALESCE(NEW.querytext,'');
	NEW.result = ''; 
	_starttime = CURRENT_TIME;
	
	IF NEW.querytext <> ''
	THEN
	   IF trim(upper(NEW.querytext)) like 'SELECT%'
	   THEN
	       query = concat(
		       'SELECT array_to_json(array_agg(row_to_json(d))) FROM (',
	           NEW.querytext, ' LIMIT 50) as d;'
		   ); 
		   EXECUTE query INTO res;
		   IF res->0 is not null
		   THEN
		      NEW.result = '<table class="schema_table" style="overflow: scroll; display: block;"><thead><tr>';
		      NEW.result = CONCAT(NEW.result, (
				SELECT 
				  string_agg(
					CONCAT(
					  '<th>', json_object_keys, '</th>'
					),''
				  )
				FROM json_object_keys(res->0)
			  ));
			  NEW.result = CONCAT(NEW.result, '</tr></thead><tbody>');
			  
			  FOR tr in (SELECT value FROM json_array_elements( res ))
			  LOOP
				NEW.result = CONCAT('<tr>', NEW.result, (SELECT 
				  string_agg(
				    CONCAT(
					  '<td style="height: 10px; white-space: nowrap;">',
					    value::varchar,
					  '</td>'),
				  '')
				FROM json_each(tr)), '</tr>');
			  END LOOP;
			  NEW.result = CONCAT(NEW.result,'</tbody></table>');
			  
		   END IF;
	   ELSE
	       EXECUTE concat(NEW.querytext,';');
		   NEW.result = 'OK';
	   END IF;
	   
	END IF;
    
    _endtime = CURRENT_TIME;
	NEW.exectime = _endtime - _starttime; 
    RETURN NEW;
END;
$$;


