-- TOC entry 559 (class 1255 OID 109572)
-- Name: tr_triggers_tr_beforafter(); Type: FUNCTION; Schema: sqlmanager; Owner: postgres
--

CREATE FUNCTION tr_triggers_tr_beforafter() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
    squery varchar;
    splus varchar;
    
BEGIN

	IF NOT NEW.noscript  
    THEN
    	IF OLD.onbefore <> NEW.onbefore
        THEN
        	splus = (
              	CASE WHEN NEW.onbefore
              	THEN 'BEFORE'
                ELSE 'AFTER'
                END
            );
        	
            IF NEW.onbefore THEN
              	NEW.onafter = false;
            ELSE
            	NEW.onafter = true;
            END IF;
        
    	ELSE 
          IF OLD.onafter <> NEW.onafter
          THEN
              splus = (
                  CASE WHEN NEW.onafter
                  THEN 'AFTER'
                  ELSE 'BEFORE'
                  END
              );
              
              IF NEW.onafter THEN
              	NEW.onbefore = false;
              ELSE
              	NEW.onbefore = true;
              END IF;
          END IF;
        END IF;
        
    	squery = CONCAT('
            -- object recreation
            DROP TRIGGER ', NEW.triggername, ' 
            ON ', NEW.tablename , ';

            CREATE TRIGGER ', NEW.triggername, '
              ', splus, ' ', sqlmanager.fn_triggeractions_str(
              	NEW.oninsert,
              	NEW.onupdate,
                NEW.ondelete,
                NEW.ontruncate
              ), ' 
              ON ', NEW.tablename , '
              
            FOR EACH ROW 
              EXECUTE PROCEDURE ', NEW.functionname , '();
        ');
        
        EXECUTE squery;
    	
    END IF;
	
	RETURN NEW;
END;
$$;


