CREATE FUNCTION tr_triggers_tr_ins() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
DECLARE
	squery varchar;
BEGIN


	IF not NEW.noscript 
    THEN
      IF COALESCE(NEW.tablename,'') = ''
      THEN 
      	PERFORM raiserror('table name is null');
      END IF;
       	
    	
      squery = concat('
          CREATE FUNCTION ',(
            SELECT t.schemaname
            FROM pg_catalog.pg_statio_all_tables as t
            WHERE CONCAT(t.schemaname,'.',t.relname) = NEW.tablename
          ),'."tr_', NEW.triggername, '" (
          )
          RETURNS trigger AS
          $body$ DECLARE BEGIN RETURN NEW; END;
          $body$
          LANGUAGE ''plpgsql''
          VOLATILE
          CALLED ON NULL INPUT
          SECURITY INVOKER;

          CREATE TRIGGER ', NEW.triggername , '
            ',(
            	CASE WHEN NEW.onbefore
                THEN 'BEFORE'
                ELSE 'AFTER'
                END
            
            ),' ',sqlmanager.fn_triggeractions_str(
              	NEW.oninsert,
              	NEW.onupdate,
                NEW.ondelete,
                NEW.ontruncate
            ),' 
            ON ', NEW.tablename , '
            
          FOR EACH ROW 
            EXECUTE PROCEDURE ',(
              SELECT t.schemaname
              FROM pg_catalog.pg_statio_all_tables as t
              WHERE CONCAT(t.schemaname,'.',t.relname) = NEW.tablename
          ),'."tr_', NEW.triggername, '"();
      
      ');
      
      EXECUTE squery;
	END IF;
	RETURN NEW;
